fun calculateFloor(floors: String) = floors.sumBy { it.toFloor() }

fun enteredBasement(floors: String) =
        floors.foldIndexed(0){ i, acc, diff ->
            val newacc = acc + diff.toFloor()
            if (newacc < 0) return i+1
            newacc
        }

fun Char.toFloor() = if ( this == '(' ) 1 else -1


